INSERT INTO `dbscript_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES 
(2000002381, 'Listen up, soldiers!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000002382, 'You are here because you displayed exceptional aptitude and ability throughout your training period.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000002383, 'You are now to be dispatched directly into a hostile environment on a treacherous mission.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000002384, 'Nagrand, the land to the west, has recently become littered with enemy forge camps that threaten our security.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000002385, 'With the bulk of our forces focused on Illidan, the task of cleansing these lands falls to you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000002386, 'You are to return in a week''s time with a detailed report of your actions.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000002387, 'May the Light be with you... Dismissed!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000002388, 'By your leave, sir, I have pressing matters to attend to.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Harbinger Erothem (Entry: 19346)'),
(2000002389, 'Of course, Harbinger. Dismissed.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 273, 'Adyen the Lightwarden (Entry: 18537)'),
(2000002390, 'Anchorite Nindumen, I have a request to make of you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Harbinger Erothem (Entry: 19346)'),
(2000002391, 'Of course, my friend. How can I be of service to you?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 6, 'Anchorite Nindumen (Entry: 19378)'),
(2000002392, 'We''ve just sent another inexperienced squad into Nagrand. Might you offer a prayer for them?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000002393, 'A noble request. It would be an honor, Erothem.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Anchorite Nindumen (Entry: 19378)'),
(2000002394, 'My friends, please join me in humble supplication to the Light.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Anchorite Nindumen (Entry: 19378)'),
(2000002395, 'Light that we embrace, we beseech thee...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000002396, 'Into our struggle against the darkness we have sent our children.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000002397, 'Please bless and protect them, and grant them success over those who seek to do us harm...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000002398, 'Continue to bless this, our ancient home, we beg.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000002399, 'Bless us with tolerance for our elven visitors.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000002400, 'By the Naaru, may it be so.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000002401, 'May it be so.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Praying Aldor NPCs'),
(2000002402, 'Sir, I am here to deliver my report from the Scryer''s Tier.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Neophyte Nemarn (Entry: 19377)'),
(2000002403, 'Well, out with it, neophyte...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Adyen the Lightwarden (Entry: 18537)'),
(2000002404, 'Sir, it''s impossible to read anything into their actions.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Neophyte Nemarn (Entry: 19377)'),
(2000002405, 'Their movements are totally chaotic, as if they themselves know not what they are about.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Neophyte Nemarn (Entry: 19377)'),
(2000002406, 'My apologies, sir. I wish I had more to report.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Neophyte Nemarn (Entry: 19377)'),
(2000002407, 'It is exactly as I expected, soldier. They know we watch them closely.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 273, 'Adyen the Lightwarden (Entry: 18537)'),
(2000002408, 'They mask their intentions with meaningless movements to confuse us.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Adyen the Lightwarden (Entry: 18537)'),
(2000002409, 'In time, you may learn to see through their deceptions.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Adyen the Lightwarden (Entry: 18537)'),
(2000002410, 'Until then, your presence at your post will keep them on their toes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Adyen the Lightwarden (Entry: 18537)'),
(2000002411, 'As you command, sir.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 273, 'Neophyte Nemarn (Entry: 19377)');
