INSERT INTO dbscript_string (entry, content_default, sound, type, language, emote, comment) VALUES 
(2000002442, 'Has he found the one, yet?', 0, 0, 0, 0, 'Samia Inkling (Entry: 21983)'),
(2000002443, 'What does the baron want?', 0, 0, 0, 0, 'Samia Inkling (Entry: 21983)'),
(2000002444, 'Ask him how he wants to proceed, then come right back!', 0, 0, 0, 0, 'Samia Inkling (Entry: 21983)'),
(2000002445, 'Hi there, cutey!  Do me a favor... ask the Baron what he wants me to do about you know what.', 0, 0, 0, 0, 'Samia Inkling (Entry: 21983)'),
(2000002446, 'Tell him that our Cenarion friends have been most helpful.', 0, 0, 0, 0, 'Samia Inkling (Entry: 21983)'),
(2000002447, 'Tell him that they have refused his \'offer\'.', 0, 0, 0, 0, 'Samia Inkling (Entry: 21983)'),
(2000002448, 'Deal with them?  How am I supposed to deal with them?', 0, 0, 0, 0, 'Samia Inkling (Entry: 21983)'),
(2000002449, 'Right.  We, uh, need to... examine... those blackwhelps.', 0, 0, 0, 0, 'Samia Inkling (Entry: 21983)'),
(2000002450, 'No.  Inform her that I\'m still searching for the prophesied one that is to assist me in the battle.  There are very few promising candidates.', 0, 0, 0, 0, 'Baron Sablemane (Entry: 22103)'),
(2000002451, 'Hello, little one.  What news do you bring?', 0, 0, 0, 0, 'Baron Sablemane (Entry: 22103)'), -- emote 6, 1, 5
(2000002452, 'Tell her that I don\'t want to be connected with it at this time.  The Cenarion Expedition should be more than happy to rid themselves of them.', 0, 0, 0, 0, 'Baron Sablemane (Entry: 22103)'),
(2000002453, 'Very well, but make certain she knows that the blackwhelps are to be captured first.', 0, 0, 0, 0, 'Baron Sablemane (Entry: 22103)'),
(2000002454, 'If they will not bend the knee, then she is to see to it that they are destroyed!', 0, 0, 0, 0, 'Baron Sablemane (Entry: 22103)'),
(2000002455, 'Must I tell her every little thing to do?  I have larger issues of my own to deal with here.', 0, 0, 0, 0, 'Baron Sablemane (Entry: 22103)'),
(2000002456, 'I did not sanction their actions.  Tell her to deal with it.', 0, 0, 0, 0, 'Baron Sablemane (Entry: 22103)'),
(2000002457, 'Tell her to use the druids for that!', 0, 0, 0, 0, 'Baron Sablemane (Entry: 22103)');
