INSERT INTO dbscript_string VALUES
(2000000191, 'Amakkar, where do you think Warlord Goretooth is going to send us? I don\'t think I can handle another tour in the Steppes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '6', NULL),
(2000000192, 'Hrmmm...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000000193, 'Quit being such a baby, Razal\'blade. It\'s time to start acting like you\'re part of one of the Warchief\'s elite forces. You don\'t want to end up like Gargal, do you?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000002547, '%s seems oblivious to the nearby commentary.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '2', '0', '0', NULL),
(2000002548, 'Proudtusk!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000002549, 'No, Gargal. Just no.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '274', NULL),
(2000002550, 'But...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000002551, 'NO! Shhh! Just shut up. Goretooth is coming!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '274', NULL),
(2000002552, '%s laughs.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '2', '0', '11', NULL),
(2000002553, 'FALL IN, MAGGOTS!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '5', NULL),
(2000002554, 'All present and accounted for, SIR!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '274', NULL),
(2000002555, 'Very well, Proudtusk, you may begin your march. Carry on!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000002556, 'SIR, YES SIR!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '66', NULL),
(2000002557, 'You heard the Warlord, maggots! MARCH!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '5', NULL),
(2000002558, 'Oh, it\'s just terribly, terribly awful! You will just DIE when you see it, Vivian.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000002559, 'Oh, do tell! The suspense alone is enough to make a girl scream!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000002560, 'Well, from what I hear, the new plague has ALL of the old plague flavor but none of the old plague calories or carbohydrates!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000002561, '%s claps excitedly.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '2', '0', '21', NULL),
(2000002562, 'Oh, how positively delightful!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000002563, 'Well hello there ladies. Heard about the new plague?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '23', NULL),
(2000002564, 'Ewwwww... not if you were the last orc on Kalimdor, Lunk.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '274', NULL),
(2000002565, '%s blushes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '2', '0', '24', NULL),
(2000002566, '%s laughs.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '2', '0', '11', NULL),
(2000002567, 'Ah, look at the time! I must return to my post... up over there.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000002568, 'If you\'ll excuse me, I must take my leave. Good day, ladies.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL);
