INSERT INTO `dbscript_string` (`entry`, `content_default`, `sound`, `type`, `language`, `emote`) VALUES
('2000002845', 'Wake up, Gal\'ressa.', '0', '0', '1', '1'),
('2000002846', '%s rubs the sleep from her eyes.', '0', '2', '0', '0'),
('2000002847', 'What do you want, Taleris?', '0', '0', '1', '0'),
('2000002848', 'You\'ve had enough time to recover from your wounds, Gal\'ressa. The time has come for you to return to the road.', '0', '0', '1', '1'),
('2000002849', 'I\'m not going on alone, Taleris, and that\'s final. I\'m going to wait here for the next group of pilgrims and set out with them when they depart.', '0', '0', '1', '1'),
('2000002850', 'You\'ll do no such thing. We simply haven\'t the room to keep you here. Now, kindly be on your way.', '0', '0', '1', '25'),
('2000002851', 'By the Sunwell, you\'ve a thick skull! You\'ll not bully me into leaving, Taleris. Now, begone from my sight!', '0', '0', '1', '274'),
('2000002852', 'Very well then, have it your way. When we turn others away, you will be responsible for their suffering. Think about that.', '0', '0', '1', '2');
